/* eslint-disable no-undef */
import React from 'react';
import moment from 'moment';
import { Modal } from '@material-ui/core';
import { MdInsertDriveFile } from 'react-icons/md';
import { Table, TableRow, TableCell } from '../../common/Table';

import './index.scss';

export const CampaignRow = props => {
  // Destructure Incoming props.
  const { rowData: { createdOn, csv, image_url, name, price, region, report } = {} } = props;
  const formattedDate = moment(createdOn).format('MMM YYYY, DD');
  const relativeDate = moment(createdOn).fromNow();

  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  console.log(price);

  const priceLabels = ['1 week - 1 Month', '6 Months', '1 Year'];
  const priceValueLabels = ['monthly', 'halfYearly', 'annually'];

  const body = (
    <div className="modal-body">
      <div className="flex mb-32">
        <img width={80} height={80} src={image_url} alt={name} />
        <div className="ml-32">
          {name}
          <br />
          <span className="text-small text-gray">{region}</span>
        </div>
      </div>
      <h4>Pricing</h4>
      {!!price &&
        !!price.length &&
        price.map((elem, index) => (
          <dl className="pricing flex mb-64">
            <dt className="flex--1">{priceLabels[index]}</dt>
            <dd>{elem[priceValueLabels[index]]}</dd>
          </dl>
        ))}
      <button type="button" onClick={handleClose}>
        Close Modal
      </button>
    </div>
  );

  return (
    <>
      <TableRow>
        <TableCell width="20%">
          <>
            {formattedDate}
            <br />
            <span className="text-small text-gray">{relativeDate}</span>
          </>
        </TableCell>
        <TableCell width="20%">
          <div className="flex">
            <img width={30} height={30} src={image_url} alt={name} />
            <div className="ml-8">
              {name}
              <br />
              <span className="text-small text-gray">{region}</span>
            </div>
          </div>
        </TableCell>
        <TableCell width="20%">
          <button className="btn-link" onClick={handleOpen}>
            View Pricing
          </button>
        </TableCell>
        <TableCell width="60%">
          <div class="flex">
            <MdInsertDriveFile size={20} className="mr-20" color=""/>
            <MdInsertDriveFile size={20} className="mr-20" />
            <MdInsertDriveFile size={20} className="mr-20"  />
          </div>
        </TableCell>
      </TableRow>
      <Modal open={open} onClose={handleClose}>
        {body}
      </Modal>
    </>
  );
};

const Campaigns = props => {
  // returns the UI for Campaigns Table.

  const getHeader = () => ['DATE', 'CAMPAIGN', 'VIEW', 'ACTIONS'];

  const { campaigns = [] } = props;

  return (
    <>
      <Table rowsData={campaigns} header={getHeader()} customRow={CampaignRow} tableHeaderSize="small" />
    </>
  );
};
export default Campaigns;
