                }
			});
		},
		[ campaigns ]
	);

	useEffect(() => {
		setIsLoading(true);
		const { getCampaigns: { url: getCampaignsURL } } = apiConfig;
		fetch(getCampaignsURL)
			.then((response) => response.json())
			.then((data) => {
				setCampaigns(data);
				setIsLoading(false);
			})
			.catch((error) => console.log(error));
	}, []);

	return (
		<div>
			<div className="campaign-list">
				{campaigns && campaigns.length ? <DashboardView data={campaigns} /> : <div> No Data Available</div>}
			</div>
			{isLoading && <Loader />}
		</div>
	);
};

export default FoodList;
