import { LIVE, PAST, UPCOMING } from '../constants';
const checkDateStatus = (date1 = new Date(), date2) => {
	let status;
	const result = date1 - date2;

	switch (result) {
		case result === 0:
			status = LIVE;
			break;
		case result < 0:
			status = UPCOMING;
			break;
		case result > 0:
			status = PAST;
			break;

		default: {
			status = LIVE;
		}
	}

	return status;
};

export { checkDateStatus };
