/* eslint-disable no-undef */
import React from 'react';
import { Table, TableRow, TableCell } from '../../common/Table';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
	table: {
		minWidth: 650
    }

});

export const CampaignRow = (props) => {
	// Destructure Incoming props.
	const { rowData: { createdOn, csv, image_url, name, price, region, report } = {}, config } = props;
	// const {
	// 	links: { deliveryRoute, contractRouteMerch } = {},
	// 	currencyMapping: { digitsAfterDecimal: currencyDigits } = {},
	// 	weightMapping: { digitsAfterDecimal: weightDigits } = {}
	// } =
	// config || {};

	return (
		<TableRow>
			<TableCell width="20%">{createdOn}</TableCell>
			<TableCell width="20%">
				<div class="flex">
					<img src={image_url} alt={name} />
					<>{`${name} ${region}`}</>
				</div>
			</TableCell>
			<TableCell width="20%">wow</TableCell>
			<TableCell width="60%">this is good</TableCell>
		</TableRow>
	);
};

const Campaigns = (props) => {
    // returns the UI for Campaigns Table.

    const classes = useStyles();


	const getHeader = () => [ 'DATE', 'CAMPAIGN', 'VIEW', 'ACTIONS' ];

	const { data = [] } = props;
	return <Table className={classes.table} rowsData={data} header={getHeader()} customRow={CampaignRow} tableHeaderSize="small" />;
};
export default Campaigns;
