import { LIVE, PAST, UPCOMING } from '../constants';
const checkDateStatus = date2 => {
  debugger;
  let status;
  const result = new Date().setHours(0, 0, 0, 0) - new Date(date2).setHours(0, 0, 0, 0);

  switch (result > 0) {
    case 1:
      status = UPCOMING;
      break;
    case 0:
        switch(result < 0)
        {
            case 1: 
            status = PAST;
            break;
            case 0:
                    status = LIVE;
                    break;
        }
      status = UPCOMING;
      break;
    case -1:
      status = PAST;
      break;
    default: {
      status = LIVE;
    }
  }

  return status;
};

export { checkDateStatus };
