import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
	table: {
		minWidth: 650
	}
});

export const CampaignRow = (props) => {
	// Destructure Incoming props.
	const {
		rowData: { amount, currencyCode, pricingLotId, quantity, scaleTicketId, lineItemNumber, contractId, deliveryTechnicalId } = {},
		config
	} = props;
	const {
		links: { deliveryRoute, contractRouteMerch } = {},
		currencyMapping: { digitsAfterDecimal: currencyDigits } = {},
		weightMapping: { digitsAfterDecimal: weightDigits } = {}
	} =
		config || {};

        return (
            <TableRow>
