/* eslint-disable no-undef */
import React from 'react';
import moment from 'moment';
import { Modal } from '@material-ui/core';
import { Table, TableRow, TableCell } from '../../common/Table';

export const CampaignRow = props => {
  // Destructure Incoming props.
  const { rowData: { createdOn, csv, image_url, name, price, region, report } = {} } = props;
  const formattedDate = moment(createdOn).format('MMM YYYY, DD');
  const relativeDate = moment(createdOn).fromNow();

  const [open, setOpen] = React.useState(false);

  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <>
      <TableRow>
        <TableCell width="20%">
          <>
            {formattedDate}
            <br />
            <span className="text-small text-gray">{relativeDate}</span>
          </>
        </TableCell>
        <TableCell width="20%">
          <div className="flex">
            <img width={30} height={30} src={image_url} alt={name} />
            <div className="ml-8">
              {name}
              <br />
              <span className="text-small text-gray">{region}</span>
            </div>
          </div>
        </TableCell>
        <TableCell width="20%">
          <button className="btn-link" onClick={handleOpen}>
            View Pricing
          </button>
        </TableCell>
        <TableCell width="60%">this is good</TableCell>
      </TableRow>
      <Modal open={open} onClose={handleClose}>
        <h1>thus is</h1>
      </Modal>
    </>
  );
};

const Campaigns = props => {
  // returns the UI for Campaigns Table.

  const getHeader = () => ['DATE', 'CAMPAIGN', 'VIEW', 'ACTIONS'];

  const { campaigns = [] } = props;

  return (
    <>
      <Table rowsData={campaigns} header={getHeader()} customRow={CampaignRow} tableHeaderSize="small" />
    </>
  );
};
export default Campaigns;
