/* eslint-disable no-undef */
import React from 'react';
import { Table, TableRow, TableCell } from '../../common/Table';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
	table: {
		minWidth: 650
	}
});

export const CampaignRow = (props) => {
	// Destructure Incoming props.
	const {
		rowData: { amount, currencyCode, pricingLotId, quantity, scaleTicketId, lineItemNumber, contractId, deliveryTechnicalId } = {},
		config
	} = props;
	const {
		links: { deliveryRoute, contractRouteMerch } = {},
		currencyMapping: { digitsAfterDecimal: currencyDigits } = {},
		weightMapping: { digitsAfterDecimal: weightDigits } = {}
	} =
		config || {};

        return (
            <TableRow>
                <TableCell width={isMobile ? '33.33%' : '58.33%'}>
                    {scaleTicketId && (
                        <Anchor
                            size="small"
                            href={`/${deliveryRoute}/${deliveryTechnicalId}`}
                            content={scaleTicketId}
                            className="text-bold"
                        />
                    )}
                </TableCell>
                <TableCell align="right" width={isMobile ? '16.67%' : '8.33%'}>
                    {totalQuantity}
                </TableCell>
                <TableCell align="right" width={isMobile ? '25%' : '16.67%'}>
                    {pricingLotId && (
                        <Anchor
                            size="small"
                            href={`/${contractRouteMerch}/${contractId}?lineItemNumber=${lineItemNumber}`}
                            content={pricingLotId}
                            className="text-bold"
                        />
                    )}
                </TableCell>
                <TableCell align="right" width={isMobile ? '25%' : '16.67%'}>{`${totalAmount
                    ? `${totalAmount} ${currencyCode}`
                    : ''}`}</TableCell>
            </TableRow>
        );

};

const Campaigns = (props) => {
    // returns the UI for Campaigns Table.

    const getHeader = () => [ { label: 'DATE', align: 'left' }, 'CAMPAIGN', 'VIEW', 'ACTIONS' ];

	const { data = [] } = props;
	return <Table columnAlignment="right" rowsData={data} header={getHeader()} customRow={CampaignRow} tableHeaderSize="small" />;
};
export default Campaigns;
