import React, { useState, useEffect } from 'react';
import axios from 'axios';

import apiConfig from '../../api.config';
import DashboardView from '../../components/Dashboard';

import Loader from '../../common/Loader';

const Dashboard = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  const scheduleCampaign = (id, value) => {
    console.log(id, 'this is id');
    let targetCampaignIndex = campaigns.findIndex(campaign => campaign.campaignId === id);
    campaigns[targetCampaignIndex].createdOn = value;
    const requestPayload = {'Content-Type': 'application/json', ...campaigns}
    axios.post

    // targetCampaign.createdOn = value;
    // setCampaigns(...campaigns, targetCampaign)
  };

  useEffect(() => {
    setIsLoading(true);
    const {
      getCampaigns: { url: getCampaignsURL }
    } = apiConfig;
    axios.get(getCampaignsURL)
      .then(response => response.json())
      .then(data => {
        setCampaigns(data);
        setIsLoading(false);
      })
      .catch(error => console.log(error));
  }, []);

  return (
    <div>
      <div className="campaign-list">
        {campaigns && campaigns.length ? (
          <DashboardView campaigns={campaigns} scheduleCampaign={scheduleCampaign} />
        ) : (
          <div> No Data Available</div>
        )}
      </div>
      {isLoading && <Loader />}
    </div>
  );
};

export default Dashboard;
