/* eslint-disable no-undef */
import React from 'react';
import { Table, TableRow, TableCell } from '../../common/Table';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
	table: {
		minWidth: 650
	}
});

export const CampaignRow = (props) => {
	// Destructure Incoming props.
	const { rowData: { createdOn, csv, image_url, name, price, region, report } = {}, config } = props;
	// const {
	// 	links: { deliveryRoute, contractRouteMerch } = {},
	// 	currencyMapping: { digitsAfterDecimal: currencyDigits } = {},
	// 	weightMapping: { digitsAfterDecimal: weightDigits } = {}
	// } =
	// config || {};

	return (
		<TableRow>
			<TableCell width="58.33%">{createdOn}</TableCell>
			<TableCell align="right" width="8.33%">
				{}`${name}`}
			</TableCell>
			<TableCell align="right" width="16.67%">
				wow
			</TableCell>
			<TableCell align="right" width="16.67%">
				this is good
			</TableCell>
		</TableRow>
	);
};

const Campaigns = (props) => {
	// returns the UI for Campaigns Table.

	const getHeader = () => [ { label: 'DATE', align: 'left' }, 'CAMPAIGN', 'VIEW', 'ACTIONS' ];

	const { data = [] } = props;
	return (
		<Table
			columnAlignment="right"
			rowsData={data}
			header={getHeader()}
			customRow={CampaignRow}
			tableHeaderSize="small"
		/>
	);
};
export default Campaigns;
