import React from 'react';
import { 	Table,
	TableRow,
	TableCell, } from '../../common/Table';
import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles({
	table: {
		minWidth: 650
	}
});

const Campaigns = (props) => {
	function createData(name, calories, fat, carbs, protein) {
		return { name, calories, fat, carbs, protein };
	}

	const rows = [
		createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
		createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
		createData('Eclair', 262, 16.0, 24, 6.0),
		createData('Cupcake', 305, 3.7, 67, 4.3),
		createData('Gingerbread', 356, 16.0, 49, 3.9)
	];
    const classes = useStyles();
    const headers = ['date', 'campaign', 'view', 'actions'];

	return (
		<TableRow>
			<TableCell width={isMobile ? '33.33%' : '58.33%'}>
				{scaleTicketId && (
					<Anchor
						size="small"
						href={`/${deliveryRoute}/${deliveryTechnicalId}`}
						content={scaleTicketId}
						className="text-bold"
					/>
				)}
			</TableCell>
			<TableCell align="right" width={isMobile ? '16.67%' : '8.33%'}>
				{totalQuantity}
			</TableCell>
			<TableCell align="right" width={isMobile ? '25%' : '16.67%'}>
				{pricingLotId && (
					<Anchor
						size="small"
						href={`/${contractRouteMerch}/${contractId}?lineItemNumber=${lineItemNumber}`}
						content={pricingLotId}
						className="text-bold"
					/>
				)}
			</TableCell>
			<TableCell align="right" width={isMobile ? '25%' : '16.67%'}>{`${totalAmount
				? `${totalAmount} ${currencyCode}`
				: ''}`}</TableCell>
		</TableRow>
	);
};
export default Campaigns;
