import { LIVE, PAST, UPCOMING } from '../constants';
const checkDateStatus = (date2) => {
	let status;
	const result = new Date().setHours(0, 0, 0, 0) - new Date(date2).setHours(0, 0, 0, 0);

	switch (result) {
		case result === 0:
			status = LIVE;
			break;
		case result < 0:
			status = UPCOMING;
			break;
		case result > 0:
			status = PAST;
			break;
		default: {
			status = LIVE;
		}
	}

	return status;
};

export { checkDateStatus };
